<?php if (!defined('THINK_PATH')) exit(); if(is_array($ad_list)): $i = 0; $__LIST__ = $ad_list;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$ad): $mod = ($i % 2 );++$i;?><div class="login-l-img fr"><a href="<?php echo ($ad["url"]); ?>" target="_blank"><img src="<?php echo attach($ad['content'],'banner');?>" width="350px" height="250px" alt="<?php echo ($ad["name"]); ?>" title="<?php echo ($ad["desc"]); ?>"></a></div><?php endforeach; endif; else: echo "" ;endif; ?>