<?php if (!defined('THINK_PATH')) exit();?><div class="top_bar"><ul id="banner_list" class="banner" style=""><?php if(is_array($ad_list)): $i = 0; $__LIST__ = $ad_list;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$ad): $mod = ($i % 2 );++$i;?><li style="background-image: url(<?php echo attach($ad['content'],'banner');?>); z-index: 1; opacity: 1; background-position: 50% 50%; background-repeat: no-repeat no-repeat;"><a target="_blank" title="<?php echo ($ad["name"]); ?>" href="<?php echo ($ad["url"]); ?>" class="pic center"></a></li><?php endforeach; endif; else: echo "" ;endif; ?></ul></div>